DROP TABLE "STORE";
CREATE TABLE "STORE" (
  store_id number NOT NULL,
  store_type varchar(45),
  store_name varchar(45),
  store_city varchar(45),
  store_state varchar(45),
  store_country varchar(45),
  coffee_bar number DEFAULT NULL,
  video_store number DEFAULT NULL,
  salad_bar number DEFAULT NULL,
  florist number DEFAULT NULL,
  PRIMARY KEY (store_id)
);
ALTER TABLE sales_fact_1997 ADD CONSTRAINT store_idx FOREIGN KEY (store_id) REFERENCES "STORE" (store_id);
commit;


CREATE TABLE promotion (
  promotion_id number NOT NULL,
  promotion_name varchar(45) NOT NULL,
  media_type varchar(45) NOT NULL,
  cost number NOT NULL,
  pstart_date varchar(100) NOT NULL,
  pend_date varchar(100) NOT NULL,
  PRIMARY KEY (promotion_id)
);
ALTER TABLE sales_fact_1997 ADD CONSTRAINT promotion_idx FOREIGN KEY (promotion_id) REFERENCES promotion (promotion_id);
commit;
